mapper.insert:
INSERT INTO PROJECT
(
    PROJECT_NAME
  , PROJECT_CATEGORY_CODE
  , PROJECT_START_DATE
  , PROJECT_END_DATE
  , PROJECT_SUMMARY
  , PROJECT_PURPOSE
  , PRODUCTOR_NO
)
VALUES
(
    #{ projectName }
  , #{ projectCategoryCode }
  , #{ startDate }
  , #{ endDate }
  , #{ projectSummary }
  , #{ projectPurpose }
  , #{ productorNo }
)

INSERT INTO JOIN_PROJECT
(
    PROJECT_NO
  , CT_USER_NO
  , RANDOM_JOIN_CODE
  , EMAIL
)
VALUES
(
    #{ projectNo }
  , -1
  , #{ joinCode }
  , #{ email }
)

mapper.update:
UPDATE PROJECT
SET

    PROJECT_NAME = #{ projectName }
  , PROJECT_CATEGORY_CODE = #{ projectCategoryCode }
  , PROJECT_START_DATE = #{ startDate }
  , PROJECT_END_DATE = #{ endDate }
  , PROJECT_SUMMARY = #{ projectSummary }
  , PROJECT_PURPOSE = #{ projectPurpose }

 WHERE PROJECT_NO = #{ projectNo }

UPDATE JOIN_PROJECT
SET
     , CT_USER_NO = #{ userNo }
     , EMAIL = #{ email }
 WHERE PROJECT_NO = #{ projectNo }
